function Card(){this.el=el("div",{className:"card"},this.img=el("img"),this.name=el("p"))}function Cards(){this.el=el("div",{className:"cards"},this.list=new List(Card,"id"))}function update(){requestAnimationFrame(update),data.sort(function(){return Math.random()-.5}),cards.update(data.slice(0,25*Math.random()+25))}var el=frzr.el,mount=frzr.mount,List=frzr.List;Card.prototype.update=function(t){this.name.textContent=t.name,this.img.src=t.img.src,this.img.width=t.img.width,this.img.height=t.img.height};for(var data=new Array(50),i=0;i<data.length;i++){var width=75*Math.random()+75,height=75*Math.random()+75;data[i]={id:i,name:"Image "+(i+1),img:{width:width,height:height,src:"https://unsplash.it/"+width+"/"+height}}}Cards.prototype.update=function(t){this.list.update(t)};var cards=new Cards;mount(document.body,cards),cards.update(data),update();