$(document).ready(function(){$("#myCarousel").carousel({interval:2e3}),$("#myCarousel").on("slid",function(){var a=$(".carousel-item.active").attr("data-slide-no");$(".myCarousel-target.active").removeClass("active"),$(".carousel-indicators [data-slide-to="+a+"]").addClass("active")}),$(".myCarousel-target").on("click",function(){$("#myCarousel").carousel(parseInt($(this).attr("data-slide-to"))),$(".myCarousel-target.active").removeClass("active"),$(this).addClass("active")})});